import ItemCount from '../ItemCount/ItemCount';

const onAdd = (clicks) =>{
    alert(`${clicks} items añadido al carrito`);
}

const ItemDetail = ({id, title, description, price, pictureUrl, stock}) => {
    return (
            <div className="cart">
                <img className="cart__image" src={pictureUrl} alt="Anillo Cómo Decírtelo Joyas" />
                <h3>{title}</h3>
                <h3>{description}</h3>
                <h4>${price}</h4>
                <ItemCount initial={1} stock={3} onAdd={onAdd} />
            </div>      
    );
}

export default ItemDetail;
