import React, {createContext, useState} from 'react';

export const CartContext = createContext()

const CartProvider = (props) =>{
    const [cartItems, setCartItems] = useState([]);
    const [qtyProducts, setQtyProducts] = useState(0);

    const addToCart = (item, quantity) => {
        let cartCopy = [...cartItems];
        if (isInCart(item.id)) {
            const found = cartItems.find (product => product.id === item.id);
            const index = cartItems.indexOf(found);
            cartCopy[index].quantity += item.quantity;
            setCartItems(cartCopy)
        }else{
        cartCopy.push({...item, quantity});
        setCartItems(cartCopy)
        }
        setQtyProducts();
    }

    const getQtyProducts = () =>{
        let quantity = 0;
        cartItems.forEach (item => quantity += item.quantity)
        setQtyProducts(quantity)
    }

    const removeItem = (id) =>{
            setCartItems (cartItems.filter(remove => remove.id !== id))
    }

    const isInCart = (id) => {
            return (cartItems.some (itemInCart => itemInCart.id === id))
    }

    const clear = () => {
        setCartItems([]);
        setQtyProducts(0)
    }

    return(
        <CartContext.Provider value={{cartItems, addToCart, qtyProducts, removeItem, isInCart, clear}}>
            {props.children}
        </CartContext.Provider>
    )
}

export default CartProvider;
