import '../ItemDetail/itemDetail.css';
import ItemCount from '../ItemCount/ItemCount';
import React, { useState } from "react";
import { Link } from "react-router-dom";
import Swal from 'sweetalert2'

const ItemDetail = ({id, title, description, price, pictureUrl, stock}) => {
    const [click, setClick] = useState(0);

    const handleAdd = (clicks) =>{
        setClick(click);


        Swal.fire({
            title: `${clicks} items añadido al carrito`,
            icon: 'success',
            html:
              'You can use <b>bold text</b>, ' +
              '<Link to={"/cart"}><button> IR AL CARRITO </button></Link> ' +
              'and other HTML tags',
            showCloseButton: true,
            showCancelButton: true,
            focusConfirm: false,
            confirmButtonText:
              '<i class="fa fa-thumbs-up"></i> Great!',
            confirmButtonAriaLabel: 'Thumbs up, great!',
            cancelButtonText:
              '<i class="fa fa-thumbs-down"></i>',
            cancelButtonAriaLabel: 'Thumbs down'
          })
    }

    return (
            <div className="detail">
                <div className='detail__image'>
                    <img className="cart__image" src={pictureUrl} alt="Anillo Cómo Decírtelo Joyas" />
                </div>    
                <div className='detail__text'>
                    <h3>{title}</h3>
                    <h4 className='detail__description'>{description}</h4>
                    <h4>${price}</h4>
                    {(click === 0) ?
                    <ItemCount initial={1} stock={3} onAdd={handleAdd} />
                    : <Link to={"/cart"}><button> IR AL CARRITO </button></Link>
                    }
                </div>    
            </div>      
    );
}

export default ItemDetail;
