import "../ItemCount/ItemCount.css"
import { useState } from 'react'

function ItemCount (props) {
    const[clicks, setClicks] = useState(props.initial);

    function decreaseClick (){
        clicks > props.initial ? setClicks(clicks-1) : console.log("mínimo disponible");
    }

    function increaseClick (){
        clicks < props.stock ? setClicks(clicks+1) : alert("no hay más stock");
    }

    return (
        <div className="itemCount">
            <button onClick={decreaseClick}> - </button>
            <div className="clicks">{clicks}</div>
            <button onClick={increaseClick}> + </button>
            <button> FINALIZAR COMPRA </button>
        </div>

    )
}

export default ItemCount;
