import React, {useContext} from "react";
import { CartContext } from "../Store/CartContext";
import { Link } from "react-router-dom";
import CartView from "./CartView";
import "../Cart/cartView.css";

const Cart = () => {
    const {cartItems, removeItem, clear} = useContext(CartContext);
    if (cartItems.length === 0) {
        return(
        <div>
        <h1>Carrito vacío</h1>
        <Link to={"/shop"}><button className="buy">Ir a Comprar </button></Link>
        </div>
        )   
    }else{
        return (
            <div className="cart">
                <table>
                    <thead>
                        <tr className="itemslist__title">
                            <th className="itemlist__remove">&nbsp;</th>
                            <th className="itemlist__img">PRODUCTO</th>
                            <th className="itemlist__title">DESCRIPCIÓN</th>
                            <th className="itemlist__price">PRECIO</th>
                            <th className="itemlist__quantity">CANTIDAD</th>
                            <th className="itemlist__subtotal">SUBTOTAL</th>
                        </tr>
                    </thead>
                </table>
                {cartItems.map ((item) =>(
                    <CartView 
                    key={item.id}
                    id={item.id}
                    title={item.title} 
                    price={item.price}
                    pictureUrl={item.pictureUrl} 
                    quantity={item.quantity} 
                    />
                ))}
                <button className="cartDeleteAll" onClick={clear}> Vaciar Carrito </button>
            </div>
        )
    }


}

export default Cart
