import "../Cart/cartView.css";
import { Link } from "react-router-dom";
import React, {useContext} from "react";
import { CartContext } from "../Store/CartContext";

const CartView = ({id, title, price, pictureUrl, quantity}) => {
    const {removeItem} = useContext(CartContext);
    return (
            <div className="cart">
                <Link to={`/detalle/${id}`}>
                    <img className="cart__image" src={pictureUrl} alt="Cómo Decírtelo Joyas" />
                </Link>
                <h3>{title}</h3>
                <h3>{quantity}</h3>
                <h4>${price}</h4>
                <button className="cartDelete" onClick={removeItem}> Eliminar del Carrito </button>
                <tr className="itemslist__title">
                    <th className="item__remove">&nbsp;</th>
                    <th className="item__img">PRODUCTO</th>
                    <th className="item__title">DESCRIPCIÓN</th>
                    <th className="item__price">PRECIO</th>
                    <th className="item__quantity">CANTIDAD</th>
                    <th className="item__subtotal">SUBTOTAL</th>
                </tr>
                <tr className="itemlist">
                <td class="product-remove">
					<img className="cartDelete" onClick={removeItem} alt="Quitar elemento"/>
                </td>
                <td class="product-thumbnail">
                <Link to={`/detalle/${id}`}><img className="cart__image" src={pictureUrl} alt="Cómo Decírtelo Joyas" /></Link>
                </td>

                </tr>
            </div>      
    );
}

export default CartView;
