import "../CartView/cartView.css";
import React, { useState, useContext } from "react";
import { CartContext } from '../Store/CartContext';


const Checkout = () => {
    const { cartItems, clear, totalPrice} = useContext(CartContext)

    const [userBuyer, setUserBuyer] = useState({
    name: "",
    phone: "",
    email: "",
    })

    const purchaseOrder = {
    buyer: { ...userBuyer },
    items: [...cartItems],
    total: totalPrice(),
    date: new Date(),
    };

    function handleSubmit(evt) {
    evt.preventDefault();
    console.log(purchaseOrder)
    setUserBuyer({
    name: "",
    email: "",
    telefono: "",
    });
    }

    function inputChangeHandler(evt) {
    const input = evt.target;

    const value = input.value;
    const inputName = input.name;

    let copyUserData = { ...userBuyer };

    copyUserData[inputName] = value;
    setUserBuyer(copyUserData);
    }

    function handleReset(evt) {
    setUserBuyer({
    name: "",
    email: "",
    telefono: "",
    });
    }

    return (
            <div className="form-container">
                <form onReset={handleReset} onSubmit={handleSubmit}>
                <div className="form-item">
                    <input
                        value={userBuyer.name}
                        onChange={inputChangeHandler}
                        name="name"
                        type="text"
                        placeholder="NOMBRE COMPLETO"
                    />
                    </div>

                    <div className="form-item">
                    <input
                        value={userBuyer.telefono}
                        onChange={inputChangeHandler}
                        name="telefono"
                        type="text"
                        placeholder="TELÃ‰FONO"
                    />
                    </div>

                    <div className="form-item">
                    <input
                        value={userBuyer.email}
                        onChange={inputChangeHandler}
                        name="email"
                        type="text"
                        placeholder="EMAIL"
                    />
                    </div>

                    <div className="form-item">
                    <input
                        value={userBuyer.email}
                        onChange={inputChangeHandler}
                        name="email"
                        type="text"
                        placeholder="CONSULTA"
                    />
                    </div>

                    <div className="forms__buttons">
                    <button className="button__form" type="submit">ENVIAR</button>
                    <button className="button__form" type="reset">CANCELAR</button>
                    </div>
                </form>
            </div>    
    );
}

export default Checkout;
